class Solution {
public:
    int findJudge(int n, vector<vector<int>>& trust) {
        if(n==0 )return -1;
        unordered_map<int,int>mp;
        for(int i=1;i<=n;i++)mp[i]=0;
        for(int i=0;i<trust.size();i++){
            mp[trust[i][0]]--;
            mp[trust[i][1]]++;
        }
        for(auto x:mp){
            if(x.second==n-1)return x.first;
        }
        return -1;
    }
};

