def main(n):
    result = []
    while n != 1:
        result.append(n)
        if n % 2 == 0:
            n //= 2
        else:
            n = n * 3 + 1
    result.append(1)
    return result

def main(dna_sequence):
    max_repetition_length = 0
    current_repetition_length = 1

    for i in range(1, len(dna_sequence)):
        if dna_sequence[i] == dna_sequence[i - 1]:
            current_repetition_length += 1
        else:
            max_repetition_length = max(max_repetition_length, current_repetition_length)
            current_repetition_length = 1

    # Check the last repetition if it's the longest
    max_repetition_length = max(max_repetition_length, current_repetition_length)

    return str(max_repetition_length)

def main(n, arr):
    moves = 0
    prev = arr[0]

    for i in range(1, n):
        if arr[i] < prev:
            moves += prev - arr[i]
        else:
            prev = arr[i]

    return str(moves)

def main(n):
