        result = [i + 1 for i in range(n)]
        for i in range(0, n - 1, 2):
            result[i], result[i + 1] = result[i + 1], result[i]
        return result
    else:
        return 'NO SOLUTION'

def main(t, tests):
    results = []
    for i in range(t):
        y, x = tests[i]
        if y >= x:
            if y % 2 == 1:
                result = y * y - x + 1
            else:
                result = (y - 1) * (y - 1) + x
        else:
            if x % 2 == 0:
                result = x * x - y + 1
            else:
                result = (x - 1) * (x - 1) + y
        results.append(result)
    return results

