def main(s):
    freq = {}
    for char in s:
        if char in freq:
            freq[char] += 1
        else:
            freq[char] = 1
    odd_count = 0
    odd_char = ''
    first_half = ''
    for char, count in freq.items():
        if count % 2 == 1:
            odd_count += 1
            odd_char = char
        first_half += char * (count // 2)
    if odd_count > 1:
        return 'NO SOLUTION'
    second_half = first_half[::-1]
    if odd_count == 1:
        return first_half + odd_char + second_half
    else:
        return first_half + second_half

def main(n):
    moves = []

    def hanoi(n, source, auxiliary, target):
        if n == 1:
            moves.append((source, target))
        else:
            hanoi(n - 1, source, target, auxiliary)
            moves.append((source, target))
            hanoi(n - 1, auxiliary, source, target)

    hanoi(n, '1', '2', '3')

    return len(moves), moves

def main(n, weights):
    total_weight = sum(weights)
    half_weight = total_weight // 2

    dp = [[False] * (half_weight + 1) for _ in range(n + 1)]
    dp[0][0] = True

    for i in range(1, n + 1):
        for j in range(half_weight + 1):
            dp[i][j] = dp[i - 1][j]
            if j >= weights[i - 1]:
                dp[i][j] = dp[i][j] or dp[i - 1][j - weights[i - 1]]

    for w in range(half_weight, -1, -1):
        if dp[n][w]:
            return total_weight - 2 * w

def main(board):
    def is_safe(row, col):
        # Check left side of the current row
        for i in range(col):
            if board[row][i] == 'Q':
                return False

        # Check upper-left diagonal
        for i, j in zip(range(row, -1, -1), range(col, -1, -1)):
            if board[i][j] == 'Q':
                return False

        # Check lower-left diagonal
        for i, j in zip(range(row, len(board), 1), range(col, -1, -1)):
            if board[i][j] == 'Q':
                return False

        return True

    def solve(row):
        if row == len(board):
            # A valid placement is found
            solutions.append(board[:])
            return

        for col in range(len(board)):
            if is_safe(row, col):
                board[row][col] = 'Q'
                solve(row + 1)
                board[row][col] = '.'

    solutions = []
    solve(0)
    return len(solutions)

def main(path_description):
    # Initialize a matrix to store the number of ways to reach each cell
    dp = [[0] * 7 for _ in range(7)]
    dp[0][0] = 1

    # Define the possible directions
    directions = [(0, 1), (0, -1), (1, 0), (-1, 0)]

    # Iterate through the path description
    for char in path_description:
        new_dp = [[0] * 7 for _ in range(7)]
        for i in range(7):
            for j in range(7):
                # Skip cells that are not reachable
                if dp[i][j] == 0:
                    continue
                if char == '?':
                    # For '?' character, distribute the ways to all possible directions
                    for dx, dy in directions:
                        ni, nj = i + dx, j + dy
                        if 0 <= ni < 7 and 0 <= nj < 7:
                            new_dp[ni][nj] += dp[i][j]
                else:
                    # Move in the specified direction
                    if char == 'D':
                        ni, nj = i + 1, j
                    elif char == 'U':
                        ni, nj = i - 1, j
                    elif char == 'L':
                        ni, nj = i, j - 1
                    else:
                        ni, nj = i, j + 1
                    if 0 <= ni < 7 and 0 <= nj < 7:
                        new_dp[ni][nj] += dp[i][j]
        dp = new_dp

    # Return the number of ways to reach the lower-left cell
    return dp[6][0]

