def main(n, x, weights):
    weights.sort()
    left, right = 0, n - 1
    gondolas = 0
    while left <= right:
        if weights[left] + weights[right] <= x:
            left += 1
            right -= 1
        else:
            right -= 1
        gondolas += 1
    return gondolas

def main(n, m, tickets, customers):
    tickets.sort()
    results = []
    for customer in customers:
        left, right = 0, n - 1
        ticket_idx = -1
        while left <= right:
            mid = (left + right) // 2
            if tickets[mid] <= customer:
                ticket_idx = mid
                left = mid + 1
            else:
                right = mid - 1
        if ticket_idx == -1:
            results.append(-1)
        else:
            results.append(tickets[ticket_idx])
            del tickets[ticket_idx]
            n -= 1
    return results

