def main(n, m, arr, operations):
    def count_rounds(arr):
        rounds = 0
        current_expected = 1
        for x in arr:
            if x == current_expected:
                current_expected += 1
            else:
                rounds += 1
        return rounds

    result = []
    rounds = count_rounds(arr)
    result.append(rounds)

    for a, b in operations:
        if arr[a - 1] > arr[b - 1]:
            arr[a - 1], arr[b - 1] = arr[b - 1], arr[a - 1]
            rounds = count_rounds(arr)
        result.append(rounds)

    return result

def main(n, songs):
    last_occurrence = {}
    longest_sequence = 0
    current_sequence = 0
    left_pointer = 0

    for right_pointer in range(n):
        song = songs[right_pointer]
        if song in last_occurrence and last_occurrence[song] >= left_pointer:
            left_pointer = last_occurrence[song] + 1

        last_occurrence[song] = right_pointer
        current_sequence = right_pointer - left_pointer + 1
        longest_sequence = max(longest_sequence, current_sequence)

    return longest_sequence

