def main(n, k, arr):
    left = max(arr)
    right = sum(arr)

    while left < right:
        mid = (left + right) // 2
        subarrays = 1
        current_sum = 0

        for num in arr:
            if current_sum + num > mid:
                subarrays += 1
                current_sum = 0
            current_sum += num

        if subarrays <= k:
            right = mid
        else:
            left = mid + 1

    return left

from collections import deque

def main(n, k, arr):
    result = []
    window = deque()

    for i in range(n):
        if i >= k:
            window.popleft()
        while window and window[-1] > arr[i]:
            window.pop()
        window.append(arr[i])

        if i >= k - 1:
            result.append(window[k // 2] if k % 2 == 1 else min(window[k // 2], window[k // 2 - 1]))

    return result

def main(n, k, arr):
    costs = []
    prefix_sum = [0]

    for x in arr:
        prefix_sum.append(prefix_sum[-1] + x)

    for i in range(n - k + 1):
        window_sum = prefix_sum[i + k] - prefix_sum[i]
        window_avg = window_sum / k
        cost = 0

        for j in range(i, i + k):
            cost += abs(arr[j] - window_avg)

        costs.append(cost)

    return costs

