def main(n, a, b, arr):
    prefix_sum = [0] * (n + 1)
    max_sum = float('-inf')
    min_prefix_sum = [0] * (n + 1)

    for i in range(n):
        prefix_sum[i + 1] = prefix_sum[i] + arr[i]

    for i in range(1, n + 1):
        min_prefix_sum[i] = min(min_prefix_sum[i - 1], prefix_sum[i])

    for len_subarray in range(a, b + 1):
        for i in range(len_subarray, n + 1):
            max_sum = max(max_sum, prefix_sum[i] - min_prefix_sum[i - len_subarray])

    return max_sum

