def main(n, x, coins):
    INF = float('inf')
    dp = [INF] * (x + 1)
    dp[0] = 0

    for i in range(1, x + 1):
        for coin in coins:
            if i - coin >= 0:
                dp[i] = min(dp[i], dp[i - coin] + 1)

    if dp[x] == INF:
        return -1
    else:
        return dp[x]

