def main(n, grid):
    MOD = 1000000007
    dp = [[0] * n for _ in range(n)]
    dp[0][0] = 1

    for i in range(n):
        for j in range(n):
            if grid[i][j] == '*':
                continue
            if i + 1 < n:
                dp[i + 1][j] = (dp[i + 1][j] + dp[i][j]) % MOD
            if j + 1 < n:
                dp[i][j + 1] = (dp[i][j + 1] + dp[i][j]) % MOD

    return dp[n - 1][n - 1]

def main(n, x, prices, pages):
    dp = [[0] * (x + 1) for _ in range(n + 1)]

    for i in range(1, n + 1):
        for j in range(x + 1):
            dp[i][j] = dp[i - 1][j]
            if j >= prices[i - 1]:
                dp[i][j] = max(dp[i][j], dp[i - 1][j - prices[i - 1]] + pages[i - 1])

    return dp[n][x]

def main(n, m, arr):
    MOD = 10**9 + 7

    dp = [[0] * (m + 1) for _ in range(n + 1)]

    for x in range(1, m + 1):
        if arr[0] == 0 or arr[0] == x:
            dp[1][x] = 1

    for i in range(2, n + 1):
        for x in range(1, m + 1):
            if arr[i - 1] == 0 or arr[i - 1] == x:
                dp[i][x] = (dp[i - 1][x - 1] + dp[i - 1][x] + dp[i - 1][x + 1]) % MOD

    return sum(dp[n]) % MOD

def main(str1, str2):
    n = len(str1)
    m = len(str2)

    # Create a 2D DP array
    dp = [[0] * (m + 1) for _ in range(n + 1)]

    # Initialize the DP array
    for i in range(n + 1):
        dp[i][0] = i
    for j in range(m + 1):
        dp[0][j] = j

    # Fill the DP array using dynamic programming
    for i in range(1, n + 1):
        for j in range(1, m + 1):
            if str1[i - 1] == str2[j - 1]:
                dp[i][j] = dp[i - 1][j - 1]
            else:
                dp[i][j] = min(dp[i - 1][j], dp[i][j - 1], dp[i - 1][j - 1]) + 1

    return dp[n][m]

def main(n, nums):
    # Initialize a 2D table to store the maximum possible score for the first player
    dp = [[0] * n for _ in range(n)]

    # Fill the table diagonally
    for i in range(n):
        dp[i][i] = nums[i]

    for length in range(2, n + 1):
        for i in range(n - length + 1):
            j = i + length - 1
            dp[i][j] = max(nums[i] - dp[i + 1][j], nums[j] - dp[i][j - 1])

    # The maximum possible score for the first player is at dp[0][n - 1]
    return dp[0][n - 1]

def main(n):
    # Calculate the total sum of numbers from 1 to n
    total_sum = (n * (n + 1)) // 2

    # If the total sum is odd, there's no way to divide it into two sets with equal sums
    if total_sum % 2 != 0:
        return 0

    # Initialize a 2D table to store the count of ways to achieve a given sum with the first i numbers
    dp = [[0] * (total_sum // 2 + 1) for _ in range(n + 1)]

    # There's one way to achieve a sum of 0, which is to have an empty set
    for i in range(n + 1):
        dp[i][0] = 1

    # Fill the dp table
    for i in range(1, n + 1):
        for j in range(1, total_sum // 2 + 1):
            dp[i][j] = dp[i - 1][j]
            if j >= i:
                dp[i][j] = (dp[i][j] + dp[i - 1][j - i]) % (10**9 + 7)

    # The answer is in dp[n][total_sum // 2]
    return dp[n][total_sum // 2]

def main(n, arr):
    # Initialize an array to store the length of the longest increasing subsequence ending at each index
    dp = [1] * n

    # Iterate through the array to calculate the longest increasing subsequence length
    for i in range(n):
        for j in range(i):
            if arr[i] > arr[j]:
                dp[i] = max(dp[i], dp[j] + 1)

    # The answer is the maximum value in the dp array
    return max(dp)

