def main(a, b):
    def count_numbers_with_no_adjacent_digits(x):
        digits = str(x)
        n = len(digits)
        dp = [[0] * 10 for _ in range(n)]

        for i in range(10):
            dp[0][i] = 1

        for i in range(1, n):
            for j in range(10):
                for k in range(10):
                    if j != k:
                        dp[i][j] += dp[i - 1][k]

        count = sum(dp[n - 1])
        return count

    count_a = count_numbers_with_no_adjacent_digits(a - 1)
    count_b = count_numbers_with_no_adjacent_digits(b)
    result = count_b - count_a
    return result

def main(n, m, grid):
    def is_valid(x, y):
        return 0 <= x < n and 0 <= y < m

    def dfs(x, y):
        if not is_valid(x, y) or grid[x][y] == '#':
            return
        grid[x][y] = '#'
        for dx, dy in [(1, 0), (-1, 0), (0, 1), (0, -1)]:
            nx, ny = x + dx, y + dy
            dfs(nx, ny)

    rooms = 0

    for i in range(n):
        for j in range(m):
            if grid[i][j] == '.':
                dfs(i, j)
                rooms += 1

    return rooms

def main(n, m, labyrinth):
    def is_valid(x, y):
        return 0 <= x < n and 0 <= y < m

    def bfs(start_x, start_y, end_x, end_y):
        queue = [(start_x, start_y, 0, '')]
        visited = set()
        directions = [(1, 0, 'D'), (-1, 0, 'U'), (0, 1, 'R'), (0, -1, 'L')]

        while queue:
            x, y, steps, path = queue.pop(0)

            if x == end_x and y == end_y:
                return 'YES', steps, path

            for dx, dy, move in directions:
                nx, ny = x + dx, y + dy
                if is_valid(nx, ny) and labyrinth[nx][ny] == '.' and (nx, ny) not in visited:
                    queue.append((nx, ny, steps + 1, path + move))
