def main(n, teleporters, queries):
    destination = [0] * (n + 1)

    for i in range(1, n + 1):
        x = i
        count = 0
        while count <= n:
            x = teleporters[x]
            count += 1
            if x == i:
                destination[i] = count
                break

    results = []

    for x, k in queries:
        if k < destination[x]:
            results.append(x)
        else:
            remaining_steps = k - destination[x]
            cycle_length = destination[x] - 1
            cycle_index = remaining_steps % cycle_length
            final_destination = x

            for i in range(cycle_index + 1):
                final_destination = teleporters[final_destination]

            results.append(final_destination)

    return results


