    mod = 10**9 + 7
    g = [[] for _ in range(n)]
    for a, b in flights:
        g[a - 1].append(b - 1)
    dp = [[0] * n for _ in range(1 << n)]
    dp[1][0] = 1
    for mask in range(1 << n):
        for u in range(n):
            if (mask >> u) & 1:
                for v in g[u]:
                    if (mask >> v) & 1 == 0:
                        dp[mask | (1 << v)][v] = (dp[mask | (1 << v)][v] + dp[mask][u]) % mod
