from functools import cmp_to_key

def main(n, points):
    def orientation(p, q, r):
        val = (q[1] - p[1]) * (r[0] - q[0]) - (q[0] - p[0]) * (r[1] - q[1])
        if val == 0:
            return 0  # Collinear
        return 1 if val > 0 else 2  # Clockwise or Counterclockwise

    def compare(p1, p2):
        o = orientation(p1, p2, points[0])
        if o == 0:
            return -1 if (p1[0] + p1[1] < p2[0] + p2[1]) else 1
        return -1 if o == 2 else 1

    if n < 3:
        return n, points

    min_idx = 0
    for i in range(1, n):
        if points[i][1] < points[min_idx][1] or (points[i][1] == points[min_idx][1] and points[i][0] < points[min_idx][0]):
            min_idx = i

    # Swap the first point with the minimum y-coordinate point
    points[0], points[min_idx] = points[min_idx], points[0]

    # Sort the remaining points based on their polar angle with respect to the first point
    points[1:] = sorted(points[1:], key=cmp_to_key(compare))

    hull = [points[0], points[1]]

    for i in range(2, n):
        while len(hull) > 1 and orientation(hull[-2], hull[-1], points[i]) != 2:
            hull.pop()
        hull.append(points[i])

    return len(hull), hull

def main(n, x, nums):
    def count_subsets_with_sum(nums, n, x):
        dp = [[0] * (x + 1) for _ in range(n + 1)]

        # Initialize the first column to 1, as there is always an empty subset with sum 0.
        for i in range(n + 1):
            dp[i][0] = 1

        for i in range(1, n + 1):
            for j in range(1, x + 1):
                if nums[i - 1] <= j:
                    dp[i][j] = dp[i - 1][j] + dp[i - 1][j - nums[i - 1]]
                else:
                    dp[i][j] = dp[i - 1][j]

        return dp[n][x]

    return count_subsets_with_sum(nums, n, x)

