def main(n, m, k, routes):
    graph = [[] for _ in range(n)]
    for a, b, r, c in routes:
        graph[a - 1].append((b - 1, r, c))

    def dijkstra(limit):
        heap = [(0, 0)]
        visited = [False] * n

        while heap:
            cost, node = heapq.heappop(heap)
            if visited[node]:
                continue
n
            visited[node] = True
            if node == n - 1:
                return cost

            for neighbor, max_parcels, parcel_cost in graph[node]:
                if parcel_cost <= limit:
                    heapq.heappush(heap, (cost + max_parcels, neighbor))

        return float('inf')

    left, right = 0, 10 ** 6
    result = float('inf')

    while left <= right:
        mid = (left + right) // 2

        if dijkstra(mid) <= k:
