        if r + h - 1 >= m or c - (h - 1) < 0 or c + (h - 1) >= n:
            return False
        for i in range(r, r + h):
            for j in range(c - (i - r), c + (i - r) + 1):
                if grid[i][j] != 1:
                    return False
        return True

    def valid_inverse_pyramid(r, c, h):
        if r - h + 1 < 0 or c - (h - 1) < 0 or c + (h - 1) >= n:
            return False
        for i in range(r - h + 1, r + 1):
