    while node:
        if node.val == query:
            mini = maxi = node.val
            break

        if node.val < query:
            mini = node.val
            node = node.right
        else:
            maxi = node.val
            node = node.left

    return [mini, maxi]


def findClosestElementsInRange(root, query):
    mini = -1
    maxi = -1

    node = root

    while node:
        if node.val == query:
            mini = maxi = node.val
            break

        if node.val < query:
            mini = node.val
            node = node.right
        else:
            maxi = node.val
            node = node.left

    if mini == query:
        node = root
        while node:
            if node.val >= query:
                maxi = node.val
                break
            node = node.right

    if maxi == query:
        node = root
        while node:
            if node.val <= query:
                mini = node.val
                break
            node = node.left

    return [mini, maxi]


